    public static Drawable fromBase64(Context context, String base64) {
        try {
            byte[] decodedBytes = Base64.decode(base64, Base64.DEFAULT);
            Bitmap bitmap = BitmapFactory.decodeByteArray(decodedBytes, 0, decodedBytes.length);
            return new BitmapDrawable(context.getResources(), bitmap);
        } catch (Exception e) {
            e.printStackTrace();
            return null;
        }
    }






    public static Drawable jc_bottom_progress(Context context) {
        // ===== Background =====
        GradientDrawable background = new GradientDrawable();
        background.setColor(0xa5ffffff); // #a5ffffff
        background.setCornerRadius(dpToPx(1, context));
        background.setSize(0, dpToPx(4, context)); // height only

        // ===== Secondary Progress =====
        GradientDrawable secondaryShape = new GradientDrawable();
        secondaryShape.setColor(0xffe0e0e0); // #ffe0e0e0
        secondaryShape.setCornerRadius(dpToPx(1, context));
        secondaryShape.setSize(0, dpToPx(4, context));

        ClipDrawable secondaryProgress = new ClipDrawable(secondaryShape, Gravity.LEFT, ClipDrawable.HORIZONTAL);

        // ===== Primary Progress =====
        GradientDrawable progressShape = new GradientDrawable();
        progressShape.setColor(0xfff85959); // #fff85959
        progressShape.setCornerRadius(dpToPx(1, context));
        progressShape.setSize(0, dpToPx(4, context));

        ClipDrawable primaryProgress = new ClipDrawable(progressShape, Gravity.LEFT, ClipDrawable.HORIZONTAL);

        // ===== LayerDrawable =====
        Drawable[] layers = new Drawable[] {
                background,           // index 0 -> background
                secondaryProgress,    // index 1 -> secondary progress
                primaryProgress       // index 2 -> primary progress
        };

        LayerDrawable layerDrawable = new LayerDrawable(layers);

        // set IDs similar to Android's internal ones
        layerDrawable.setId(0, android.R.id.background);
        layerDrawable.setId(1, android.R.id.secondaryProgress);
        layerDrawable.setId(2, android.R.id.progress);

        return layerDrawable;
    }

    public static Drawable jc_bottom_seek_progress(Context context) {
        // قيم XML
        int height = dpToPx(1, context);
        float radius = dpToPx(1.5f, context);

        // ===== Background =====
        GradientDrawable background = new GradientDrawable();
        background.setColor(0xa5ffffff); // #a5ffffff
        background.setCornerRadius(radius);
        background.setSize(0, height); // width=0 so it stretches

        // ===== Secondary Progress =====
        GradientDrawable secondaryShape = new GradientDrawable();
        secondaryShape.setColor(0xffffffff); // #ffffffff
        secondaryShape.setCornerRadius(radius);
        secondaryShape.setSize(0, height);

        ClipDrawable secondaryProgress = new ClipDrawable(
                secondaryShape,
                Gravity.LEFT,
                ClipDrawable.HORIZONTAL
        );

        // ===== Primary Progress =====
        GradientDrawable progressShape = new GradientDrawable();
        progressShape.setColor(0xfff85959); // #fff85959
        progressShape.setCornerRadius(radius);
        progressShape.setSize(0, height);

        ClipDrawable primaryProgress = new ClipDrawable(
                progressShape,
                Gravity.LEFT,
                ClipDrawable.HORIZONTAL
        );

        // ===== Combine into LayerDrawable =====
        Drawable[] layers = new Drawable[]{
                background,
                secondaryProgress,
                primaryProgress
        };

        LayerDrawable layerDrawable = new LayerDrawable(layers);
        layerDrawable.setId(0, android.R.id.background);
        layerDrawable.setId(1, android.R.id.secondaryProgress);
        layerDrawable.setId(2, android.R.id.progress);

        return layerDrawable;
    }


    public static Drawable jc_bottom_seek_thumb(Context context) {
        StateListDrawable selector = new StateListDrawable();

        Drawable pressed = jc_seek_thumb_pressed(context);
        Drawable normal = jc_seek_thumb_normal(context);

        selector.addState(new int[]{android.R.attr.state_pressed}, pressed);
        selector.addState(new int[]{}, normal);

        return selector;
    }


