    public static Drawable jc_dialog_progress(Context context) {
        float radius = dpToPx(2, context);

        // ===== Background =====
        GradientDrawable background = new GradientDrawable();
        background.setColor(0xFFFFFFFF); // #ffffffff
        background.setCornerRadius(radius);

        // ===== Progress =====
        GradientDrawable progressShape = new GradientDrawable();
        progressShape.setColor(0xFFF85959); // #fff85959
        progressShape.setCornerRadius(radius);

        ClipDrawable progress = new ClipDrawable(
                progressShape,
                Gravity.LEFT,
                ClipDrawable.HORIZONTAL
        );

        // ===== Combine layers =====
        Drawable[] layers = new Drawable[]{
                background,
                progress
        };

        LayerDrawable layerDrawable = new LayerDrawable(layers);
        layerDrawable.setId(0, android.R.id.background);
        layerDrawable.setId(1, android.R.id.progress);

        return layerDrawable;
    }

    public static Drawable jc_dialog_progress_bg(Context context) {
        float radius = dpToPx(6, context);

        GradientDrawable shape = new GradientDrawable();
        shape.setColor(0xcc000000); // #cc000000
        shape.setCornerRadii(new float[]{
                radius, radius,   // top-left
                radius, radius,   // top-right
                radius, radius,   // bottom-right
                radius, radius    // bottom-left
        });

        return shape;
    }

    public static Drawable jc_loading(Context context) {
        Drawable innerDrawable = fromBase64(context, drawables.jc_loading_bg_png);

        RotateDrawable rotateDrawable = new RotateDrawable();
        rotateDrawable.setDrawable(innerDrawable);
        rotateDrawable.setFromDegrees(0.0f);
        rotateDrawable.setToDegrees(360.0f);
        rotateDrawable.setPivotX(0.5f); // 50%
        rotateDrawable.setPivotY(0.5f); // 50%

        return rotateDrawable;
    }

