    public static Drawable jc_volume_progress_bg(Context context) {
        float radius = dpToPx(2, context);

        // ===== Background =====
        GradientDrawable background = new GradientDrawable();
        background.setColor(0xffffffff); // #ffffffff
        background.setCornerRadius(radius);

        // ===== Progress (clip vertically from bottom) =====
        GradientDrawable progressShape = new GradientDrawable();
        progressShape.setColor(0xfff85959); // #fff85959
        progressShape.setCornerRadius(radius);

        ClipDrawable progress = new ClipDrawable(
                progressShape,
                Gravity.BOTTOM,  // progress appears from bottom to top
                ClipDrawable.VERTICAL
        );

        // ===== Combine layers =====
        Drawable[] layers = new Drawable[]{
                background,
                progress
        };

        LayerDrawable layerDrawable = new LayerDrawable(layers);
        layerDrawable.setId(0, android.R.id.background);
        layerDrawable.setId(1, android.R.id.progress);

        return layerDrawable;
    }

    private static int dpToPx(float dp, Context context) {
        return (int) (dp * context.getResources().getDisplayMetrics().density + 0.5f);
    }


